  `comment` text NOT NULL,
  `comment_date` datetime NOT NULL,
  `comment_flag` int(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=latin1;

